<?php$jws_theme_options = $GLOBALS['jws_theme_options'];$image_default = isset($jws_theme_options['jws_theme_blog_image_default']) ? $jws_theme_options['jws_theme_blog_image_default'] : '';if(is_home()){	$jws_theme_show_post_title = 1;	$jws_theme_show_post_desc = 1;	$jws_theme_show_post_info = 1;}elseif (is_single()) {	$jws_theme_blog_crop_image = isset($jws_theme_options['jws_theme_post_crop_image']) ? $jws_theme_options['jws_theme_post_crop_image'] : 0;	$jws_theme_blog_image_width = (int) isset($jws_theme_options['jws_theme_post_image_width']) ? $jws_theme_options['jws_theme_post_image_width'] : 800;	$jws_theme_blog_image_height = (int) isset($jws_theme_options['jws_theme_post_image_height']) ? $jws_theme_options['jws_theme_post_image_height'] : 400;	$jws_theme_show_post_title = (int) isset($jws_theme_options['jws_theme_post_show_post_title']) ? $jws_theme_options['jws_theme_post_show_post_title'] : 1;	$jws_theme_show_post_info = (int) isset($jws_theme_options['jws_theme_post_show_post_info']) ? $jws_theme_options['jws_theme_post_show_post_title'] : 1;	$jws_theme_post_show_social_share = (int) isset($jws_theme_options['jws_theme_post_show_social_share']) ? $jws_theme_options['jws_theme_post_show_social_share'] : 1;	$jws_theme_post_show_post_tags = (int) isset($jws_theme_options['jws_theme_post_show_post_tags']) ? $jws_theme_options['jws_theme_post_show_post_tags'] : 1;	$jws_theme_post_show_post_author = (int) isset($jws_theme_options['jws_theme_post_show_post_author']) ? $jws_theme_options['jws_theme_post_show_post_author'] : 1;	$jws_theme_show_post_desc = 1;}else{ //archive 	$jws_theme_blog_crop_image = isset($jws_theme_options['jws_theme_blog_crop_image']) ? $jws_theme_options['jws_theme_blog_crop_image'] : 0;	$jws_theme_blog_image_width = (int) isset($jws_theme_options['jws_theme_blog_image_width']) ? $jws_theme_options['jws_theme_blog_image_width'] : 600;	$jws_theme_blog_image_height = (int) isset($jws_theme_options['jws_theme_blog_image_height']) ? $jws_theme_options['jws_theme_blog_image_height'] : 400;	$jws_theme_show_post_title = (int) isset($jws_theme_options['jws_theme_blog_show_post_title']) ? $jws_theme_options['jws_theme_blog_show_post_title'] : 1;	$jws_theme_show_post_info = (int) isset($jws_theme_options['jws_theme_blog_show_post_info']) ? $jws_theme_options['jws_theme_blog_show_post_title'] : 1;	$jws_theme_show_post_desc = (int) isset($jws_theme_options['jws_theme_blog_show_post_desc']) ? $jws_theme_options['jws_theme_blog_show_post_desc'] : 1;	$jws_theme_blog_post_excerpt_leng = (int) isset($jws_theme_options['jws_theme_blog_post_excerpt_leng']) ? $jws_theme_options['jws_theme_blog_post_excerpt_leng'] : 100;	$jws_theme_blog_post_excerpt_more = isset($jws_theme_options['jws_theme_blog_post_excerpt_more']) ? $jws_theme_options['jws_theme_blog_post_excerpt_more'] : '...';	$jws_theme_blog_post_readmore = isset($jws_theme_options['jws_theme_blog_post_excerpt_more']) ? $jws_theme_options['jws_theme_blog_post_readmore'] :  '';}$jws_theme_blog_post_excerpt_leng = (int) isset($jws_theme_options['jws_theme_blog_post_excerpt_leng']) ? $jws_theme_options['jws_theme_blog_post_excerpt_leng'] : 100;$jws_theme_blog_post_excerpt_more = isset($jws_theme_options['jws_theme_blog_post_excerpt_more']) ? $jws_theme_options['jws_theme_blog_post_excerpt_more'] : '...';$jws_theme_blog_post_readmore = isset($jws_theme_options['jws_theme_blog_post_excerpt_more']) ? $jws_theme_options['jws_theme_blog_post_readmore'] :  '';$readmore_block = 0;	?><article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>	<div class="tb-post-item">	    <?php if (has_post_thumbnail()) { ?>	        <div class="tb-thumb">	            <!-- Get Thumb -->	            <?php the_post_thumbnail('medicare-blog-large-hard-crop', array('class'=>'img-responsive')); ?>					        </div>	    <?php } ?>								<div class="tb-content">					<?php  //from blog grid tpl1			if($jws_theme_show_post_title) { ?>				<a href="<?php the_permalink(); ?>"><h4 class="tb-title"><?php the_title(); ?></h4></a>			<?php } ?>			<?php if($jws_theme_show_post_desc) { ?>				<div class="tb-excerpt">					<?php echo jws_theme_custom_excerpt(intval($jws_theme_blog_post_excerpt_leng), $jws_theme_blog_post_excerpt_more); ?>				</div>			<?php } ?>			<?php if($jws_theme_blog_post_readmore) { ?>				<a class="tb-readmore <?php if( $readmore_block ) echo ' block';?>" href="<?php the_permalink(); ?>"><?php echo esc_attr( $jws_theme_blog_post_readmore ); ?></a>			<?php } ?>			<!-- Date -->			<?php if($jws_theme_show_post_info) { ?>			<div class ="more-info">				<?php 				$archive_year  = get_the_time('Y'); 				$archive_month = get_the_time('m'); 				$archive_day   = get_the_time('d'); 				$archive_all = get_the_time('');   				?>				<a href="<?php echo esc_url(get_day_link($archive_year, $archive_month, $archive_day)); ?>">					<span class="blog-date"><?php echo get_the_time('M').' '.get_the_time('d').'. '.get_the_time('Y').'. ';?></span></a>					<span class="author-name"><?php esc_html_e('by ', 'medicare'); the_author_posts_link(); ?></span>			</div>			<?php } ?>		</div>		</div></article>